    <>
      <div className="backgroundAnim">
        <div className="w-full h-full bg-black/75">
          { loading ? <LoadS /> : null}
          <div className="">
            { loading ? null : <div className="flex flex-row">
              <div className="basis-1/3">
                <InputSide />
              </div>
              <div className="basis-2/3">
                <TableSide />
              </div>
            </div> }
            <div className="">
              <Footer />
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
